	public static synchronized HashMap<String, String> getListOfBondsFromURL(String url) throws Exception {
		HashMap<String, String> map = new HashMap<String, String>();
//		String urlOfForeignBondsList="https://www.tinkoff.ru/invest/bonds/?country=Foreign&orderType=Desc&sortType=ByYieldToClient&start=0&end=220";
		String html = TelegramBotLib.getURLContent(url);
		String splitDivider = "<div class=\"SecurityColumn__nameContainer_11tNb\" data-qa-file=\"NameColumn\">";
		boolean firstIteration = true;
		Pattern pattern = Pattern.compile(".*nameWrapper_1r9VF\" data-qa-file=\"NameColumn\">(?<name>.*)<\\/span>.*data-qa-file=\"Caption\">(?<ticker>.*)<\\/div><\\/div><\\/div><\\/div><\\/div><\\/div><\\/span><\\/a><\\/td>.*");
		Matcher matcher;
		for(String row : html.split(splitDivider)) {
			if(firstIteration) {
				firstIteration=false;
				continue;
			}
			row = splitDivider + row;
//			System.out.println(row);
			if((matcher=pattern.matcher(row)).find()) {
//				System.err.println("name: " + matcher.group("name") + ", ticker: " + matcher.group("ticker"));
				map.put(matcher.group("ticker"), matcher.group("name"));
			}else {
				throw new Exception("Can't find bond's name and ticker from url ["+url+"]");
			}
		}
		return map;
	}

